var mixer;
var clock = new THREE.Clock();
var loader = new THREE.FBXLoader();
var url = "/app/alpha/001/fbx/chh_CatwalkWalkCollection_mxr_v2.5.2.fbx";

loader.load( url, function( object ){

//	object is a THREE.Group.

	object.name = "Charlotte";
	console.log( object ); 

	animations = object.animations;

	if ( animations.length ) {

		mixer = new THREE.AnimationMixer( object );

		if ( animations.length > 1 ) {
			var action = mixer.clipAction( animations[1], object );
			action.play();
		} else {
			var action = mixer.clipAction( animations[0], object )
			action.play();
		}

	}

//

	object.children.forEach( function( item ){
		if ( item.type != "SkinnedMesh" ) return;
		if ( Array.isArray( item.material ) ) 
			for ( var i = 0; i < item.material.length; i++ ) {
				item.material[i] = skinningMaterial();
			}
		else
			item.material = skinningMaterial();
	});

	body = object.getObjectByName( "Body" );
	hair = object.getObjectByName( "Hair" );
	dress = object.getObjectByName( "Dress" );
	shoes = object.getObjectByName( "Shoes" );
	underwears = object.getObjectByName( "Underwears" );

//

	fbx = object;
	scene.add( object );
	controls.focus( object, true );

	function skinningMaterial(){
		return new THREE.MeshStandardMaterial({
			side: 2,
			skinning: true,
		});
	}

});

function update( event ) {

	var dt = clock.getDelta();
	mixer && mixer.update( dt );


}
