var mixer;
var clock = new THREE.Clock();
var loader = new THREE.FBXLoader();
var url = "/charlotte/models/fbx/chh_CatwalkWalkCollection_mxr_v2.5.1.fbx";

loader.load( url, function( object ){

//	object is a THREE.Group.

	object.name = "Charlotte";
	console.log( object ); 

	animations = object.animations;

	if ( animations.length ) {

		mixer = new THREE.AnimationMixer( object );

		if ( animations.length > 1 ) {
			var action = mixer.clipAction( animations[1], object );
			action.play();
		} else {
			var action = mixer.clipAction( animations[0], object )
			action.play();
		}

	}

	fbx = object;
	scene.add( object );
	controls.focus( object, true );


});

function update( event ) {

	var dt = clock.getDelta();
	mixer && mixer.update( dt );


}
