      case KEY_S :
      case KEY_DOWN :
        this.isDown = true;
        break;

      case KEY_A :
      case KEY_LEFT :
        this.isLeft = true;
        break;

      case KEY_D :
      case KEY_RIGHT :
        this.isRight = true;
        break;

      case KEY_SPACE :
        this.jump();
        break;

      default:
        return;

    }

    var prevAngle = this.frontAngle;

    this.updateAngle();

    if ( prevAngle !== this.frontAngle ) {

      this.dispatchEvent( { type: 'movekeychange' } );

    }

