      case KEY_S :
      case KEY_DOWN :
        this.isDown = false;
        break;

      case KEY_A :
      case KEY_LEFT :
        this.isLeft = false;
        break;

      case KEY_D :
      case KEY_RIGHT :
        this.isRight = false;
        break;

      case KEY_SPACE :
        break;

      case KEY_R:
        this.inRun = !this.inRun; // hack //  console.log("case KEY_R:", e.keyCode, this.inRun);
        break;

      default:
        return;

    }

    var prevAngle = this.frontAngle;

    this.updateAngle();

    if ( prevAngle !== this.frontAngle ) {

      this.dispatchEvent( { type: 'movekeychange' } );

    }

    if ( !this.isUp && !this.isDown && !this.isLeft && !this.isRight &&
      (
           e.keyCode === KEY_W
        || e.keyCode === KEY_UP
        || e.keyCode === KEY_S
        || e.keyCode === KEY_DOWN
        || e.keyCode === KEY_A
        || e.keyCode === KEY_LEFT
        || e.keyCode === KEY_D
        || e.keyCode === KEY_RIGHT
      )
    ) {

      this.isMoveKeyHolded = false;
      this.dispatchEvent( { type: 'movekeyoff' } );

    }

  }

  function onblur ( e ) {

    this.isUp    = false;
    this.isDown  = false;
    this.isLeft  = false;
    this.isRight = false;
//  this.inRun   = false; // hack // DO NOT SET TO FALSE ON BLUR. //

    if ( this.isMoveKeyHolded ) {

      this.isMoveKeyHolded = false;
      this.dispatchEvent( { type: 'movekeyoff' } );

    }

