fbx = null; // global.
mixer = null; // global.

var animations;
var clock = new THREE.Clock();
var loader = new THREE.FBXLoader();
var url = "/app/alpha/002/model/Caroline_v3.fbx";

loader.load( url, function( object ){

//	object is a THREE.Group.
	console.log( object ); 
	object.name = "Caroline";

//	Caroline_v2.fbx animations.

	var animations = object.animations;
	console.log( object.animations.map(function(item){return item.name.replace("Caroline|", "").replace(/\s/g, "")}).join("\n") )

	mixer = new THREE.AnimationMixer( object );

//	Caroline_v3.fbx animation order.

	Jumping            =  mixer.clipAction( animations[0], object );
	CatwalkWalkForward =  mixer.clipAction( animations[1], object );
	LowCrawl           =  mixer.clipAction( animations[2], object );
	Walking            =  mixer.clipAction( animations[3], object );
	RunningJump        =  mixer.clipAction( animations[4], object );
	FastRun            =  mixer.clipAction( animations[5], object );
	StandardRun        =  mixer.clipAction( animations[6], object );
	Jump2              =  mixer.clipAction( animations[7], object );
	Jogging            =  mixer.clipAction( animations[8], object );
	MediumRun          =  mixer.clipAction( animations[9], object );
	StandingIdle       = mixer.clipAction( animations[10], object );
	CatwalkWalking     = mixer.clipAction( animations[11], object );
	RunningBackward    = mixer.clipAction( animations[12], object );
	TreadmillRunning   = mixer.clipAction( animations[13], object );
	FallDown           = mixer.clipAction( animations[14], object );
	Walk               = mixer.clipAction( animations[15], object );
	Jump1              = mixer.clipAction( animations[16], object );
	StandingJump       = mixer.clipAction( animations[17], object );
	RunForward         = mixer.clipAction( animations[18], object );
	LocomotionPose     = mixer.clipAction( animations[19], object );

//	skinned.

	bra = object.getObjectByName( "Bra" );
	hair = object.getObjectByName( "Hair" );
	body = object.getObjectByName( "Body" );
	torso = object.getObjectByName( "Torso" );
	skirt = object.getObjectByName( "Skirt" );
