AW3D.PlayerSphere = function ( name ){
	var sphere = new THREE.Mesh(
		new THREE.SphereGeometry( 15, 8, 4 ),
		new THREE.MeshBasicMaterial({ 
			color: 0xff0000,  
			wireframe: true,
		})
	); 
