//	Animation.

setTimeout( function(){

	var button = document.createElement("div");
	button.textContent = "Idling";
	button.classList.add("btn", "btn-warning", "form-control");
	button.addEventListener( "click", function(){
		if ( StandingIdle.isRunning() ) return;
		var wasPlaying = StandingIdle.isRunning();
		mixer.stopAllAction();
		if ( !wasPlaying ) {
			StandingIdle.play();
		}
	});

	container.appendChild( button );

});


setTimeout( function(){

	var button = document.createElement("div");
	button.textContent = "Walking";
	button.classList.add("btn", "btn-warning", "form-control");
	button.addEventListener( "click", function(){
		var wasPlaying = CatwalkWalking.isRunning();
		mixer.stopAllAction();
		if ( !wasPlaying ) {
			CatwalkWalking.play();
		} else {
			StandingIdle.play();
		}
	});

	container.appendChild( button );

});


